function Game(socketObj)
{
    socket = socketObj;
}

let socket;
let frameStartTime = 0;
let deltaTime = 0;
console.log('base module');

Game.Start = function()
{
    frameStartTime = Date.now();
    this.Update();
}

Game.Update = function()
{
    deltaTime = Date.now() - frameStartTime;
    frameStartTime = Date.now();
    process.nextTick(this.Update);
}

module.exports = Game;