
var PlayerManager = function ()
{

    var players = {};

    PlayerManager.prototype.AddPlayer = function(socketID)
    {

        players[socketID] =
        {
            playerID : socketID,
            score : 0,
            rotation: 0,
            //x: Math.floor(Math.random() * 700) + 50,
            //y: Math.floor(Math.random() * 500) + 50
            x: 0,
            y :0
        };
    }

    PlayerManager.prototype.RemovePlayer = function(socketID)
    {
        delete players[socketID];
    }   
    

    PlayerManager.prototype.GetPlayers = function()
    {
        return players;
    }

    PlayerManager.prototype.GetPlayer = function(socketID)
    {
        
        return players[socketID];
    }

    PlayerManager.prototype.SaySomething = function()
    {
        console.log('Player manager');
    }

}

module.exports = PlayerManager;
