class ConnectionObject{
    constructor(io,socketID){
        this.socketID = socketID;
        this.isAway = false;
        
        this.setupHandlers(io);
    }

    setupHandlers(io){
        io.sockets.connected[this.socketID].on('disconnecting', () => {
            this.isAway = true;
        });
    }

}
module.exports = ConnectionObject;