class ConnectionObject{
    constructor(io, socketID){
        this.io = io;
        this.socketID = socketID;
        this.isAway = false;
        
        this.setupHandlers();
    }

    setupHandlers(){
        this.getSocketObject(this.socketID).on('disconnected', () => {
            this.isAway = true;
        });
    }

    getSocketObject(){
        if(io !== undefined){
            let socketObj = io.sockets.connected[socketID];
            return socketObj;
        }
    }
}
module.exports = ConnectionObject;