var PassTheBomb = require('games/PassTheBomb');
var MonsterRaid = require('games/MonsterRaid');
var StopBullyingMe = require('games/StopBullyingMe');
var RedLightGreenLight = require('games/RedLightGreenLight');

// update new games here:
const classes = {
    PassTheBomb,
    MonsterRaid,
    StopBullyingMe,
    RedLightGreenLight
};

// proxy class
class DynamicGameClass {
    constructor (className, opts) {
        if(classes[className] !== undefined){
            return new classes[className](opts);
        } else {
            console.log('Cannot find class ' + className);
            return undefined;
        }
    }
}

module.exports =  DynamicGameClass;