function Player(socketID)
{
    let playerID = socketID;
    let score = 0;
    let isHost = false;
    
    Player.prototype.AddScore = function(amt)
    {
        score += amt;
    }

    Player.prototype.GetScore = function()
    {
        return score;
    }

    Player.prototype.MinusScore = function(amt)
    {
        score -= amt;
    }

    Player.prototype.GetPlayerID = function()
    {
        return playerID;
    }

    Player.prototype.GetIsHost = function()
    {
        return isHost;
    }
    
    Player.prototype.SetHost = function(x)
    {
        isHost = x;
    }

}

module.exports = Player;