var Room = require('server/Room');

class RoomManager{
    constructor(){
        this.gamerooms = {};
    }

    createRoom(roomCode){
        let room = new Room(roomCode);
        gamerooms[roomCode] = room;

        return room;
    }
    
    onDisconnect(socketID){
        Object.keys(gamerooms).forEach((code) => {
            gamerooms[code].onDisconnect(socketID);
        });
    }


}
module.exports = RoomManager;