function RoomUtils(){}

RoomUtils.CreateRoom = function()
{
    let players = [];
    let games = [];

    let createdRoom =
    {
        _players : players,
        _games: games
    }

    return createdRoom;

}

RoomUtils.CreatePlayer = function(socketID)
{
    let createdPlayer =
    {
        _playerID : socketID,
        _score : 0,
        _isHost : false
    }

    return createdPlayer;
}



module.exports = RoomUtils;