var ConnectionObject = require('server/ConnectionObject');

class User extends ConnectionObject{
    constructor(io, socketID, userID, score, name, isHost){
        super(io, socketID);
        this.userID = userID;
        this.score = score;
        this.name = name;
        this.isHost = isHost;
        this.isInitialized = false;
    }
}
module.exports = User;